mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM rn FROM ( SELECT r.r_num, r.u_member_id, r.pd_id, r.r_score, r.r_content, r.r_like_cnt, r.r_regDate, u.u_nickname AS u_nickname FROM review_tbl r LEFT JOIN user_tbl u ON u.u_member_id = r.u_member_id
AND r.pd_id = #{pd_id}
ORDER BY r.r_regDate DESC ) A WHERE ROWNUM <= #{end} ) WHERE rn >= #{start}

SELECT COUNT(*)
  FROM review_tbl
 WHERE pd_id = #{value}

SELECT COUNT(*) AS cnt
  FROM review_tbl
 WHERE r_num = #{r_num}
   AND u_member_id = #{u_member_id}

mapper.update:
UPDATE review_tbl
   SET r_score   = #{r_score},
       r_content = #{r_content}
 WHERE r_num = #{r_num}
