mapper.select:
SELECT u_nickname
FROM user_tbl
WHERE u_id = #{u_id}

mapper.insert:
INSERT INTO board_tbl (b_num, u_member_id, b_title, b_contents, b_category, b_dateposted, b_updateDate, b_views, b_recommend, b_image) VALUES ((SELECT NVL(MAX(b_num)+1,1) FROM board_tbl), #{u_member_id}, #{b_title}, #{b_contents}, #{b_category}, sysdate, NULL, 0, 0,
#{b_image}
null
)

mapper.select:
SELECT u_member_id
FROM user_tbl
where u_id = #{u_id}

SELECT MAX(b_num)
FROM board_tbl
WHERE u_member_id = #{u_member_id}

mapper.update:
UPDATE board_tbl
SET b_views = b_views + 1
WHERE b_num = #{b_num}

mapper.select:
SELECT COUNT(*)
FROM recommend_tbl
WHERE u_member_id = #{u_member_id}
AND b_num = #{b_num}

mapper.delete:
DELETE FROM recommend_tbl
WHERE b_num = #{b_num}
AND u_member_id = #{u_member_id}

mapper.update:
UPDATE board_tbl
SET b_recommend = ( SELECT COUNT(*)
FROM recommend_tbl
WHERE b_num = #{b_num}
)
WHERE b_num = #{b_num}

mapper.select:
SELECT b_recommend
FROM board_tbl
WHERE b_num = #{b_num}

